mapper.select:
select 

	from CADRIVER 
	where CADRIVER.lineid in (

		select 
		CADRIVER.lineid
	    from CADRIVER 

	        and CADRIVER.lineid = #{entity.lineid}
	        and CADRIVER.fleetid = #{entity.fleetid}
	        and CADRIVER.driverno = #{entity.driverno}
	        and CADRIVER.driverlicense = #{entity.driverlicense}
	        and CADRIVER.drivername = #{entity.drivername}
	        and CADRIVER.cardno = #{entity.cardno}
	        and CADRIVER.homeaddress = #{entity.homeaddress}
	        and CADRIVER.issuingorg = #{entity.issuingorg}
	        and CADRIVER.getcarddate = #{entity.getcarddate}
	        and CADRIVER.mobile = #{entity.mobile}
	        and CADRIVER.hometel = #{entity.hometel}
	        and CADRIVER.contact = #{entity.contact}
	        and CADRIVER.tel = #{entity.tel}
	        and CADRIVER.relationship = #{entity.relationship}
	        and CADRIVER.memo = #{entity.memo}
	        and CADRIVER.drivertype = #{entity.drivertype}
	        and CADRIVER.dtdate = #{entity.dtdate}
	        and CADRIVER.userno = #{entity.userno}
	        and CADRIVER.active = #{entity.active}
	        and CADRIVER.locationdate = #{entity.locationdate}
	        and CADRIVER.province = #{entity.province}
	        and CADRIVER.city = #{entity.city}
	        and CADRIVER.address = #{entity.address}
	        and CADRIVER.locationmemo = #{entity.locationmemo}


)

