mapper.select:
select 

	from CAFLEET 
	where CAFLEET.lineid in (

		select 
		CAFLEET.lineid
	    from CAFLEET 

	        and CAFLEET.lineid = #{entity.lineid}
	        and CAFLEET.fleetno = #{entity.fleetno}
	        and CAFLEET.fleetname = #{entity.fleetname}
	        and CAFLEET.fleettype = #{entity.fleettype}
	        and CAFLEET.contact = #{entity.contact}
	        and CAFLEET.tel = #{entity.tel}
	        and CAFLEET.cityname = #{entity.cityname}
	        and CAFLEET.memo = #{entity.memo}
	        and CAFLEET.dtdate = #{entity.dtdate}
	        and CAFLEET.userno = #{entity.userno}
	        and CAFLEET.active = #{entity.active}
	        and CAFLEET.bankname = #{entity.bankname}
	        and CAFLEET.bankbillno = #{entity.bankbillno}
	        and CAFLEET.invoicecompany = #{entity.invoicecompany}


)

