mapper.select:
select 

	from ODORDER 
	where ODORDER.lineid in (

		select 
		ODORDER.lineid
	    from ODORDER 

	        and ODORDER.lineid = #{entity.lineid}
	        and ODORDER.orderno = #{entity.orderno}
	        and ODORDER.custshipno = #{entity.custshipno}
	        and ODORDER.customerid = #{entity.customerid}
	        and ODORDER.contractid = #{entity.contractid}
	        and ODORDER.userno = #{entity.userno}
	        and ODORDER.dtdate = #{entity.dtdate}
	        and ODORDER.address = #{entity.address}
	        and ODORDER.contact = #{entity.contact}
	        and ODORDER.tel = #{entity.tel}
	        and ODORDER.address2 = #{entity.address2}
	        and ODORDER.contact2 = #{entity.contact2}
	        and ODORDER.tel2 = #{entity.tel2}
	        and ODORDER.startprovince = #{entity.startprovince}
	        and ODORDER.startcityid = #{entity.startcityid}
	        and ODORDER.startcity = #{entity.startcity}
	        and ODORDER.endprovince = #{entity.endprovince}
	        and ODORDER.endcityid = #{entity.endcityid}
	        and ODORDER.endcity = #{entity.endcity}
	        and ODORDER.shipdate = #{entity.shipdate}
	        and ODORDER.comedate = #{entity.comedate}
	        and ODORDER.urgent = #{entity.urgent}
	        and ODORDER.itemid = #{entity.itemid}
	        and ODORDER.itemno = #{entity.itemno}
	        and ODORDER.itemname = #{entity.itemname}
	        and ODORDER.unit = #{entity.unit}
	        and ODORDER.qty = #{entity.qty}
	        and ODORDER.shipedqty = #{entity.shipedqty}
	        and ODORDER.pid = #{entity.pid}
	        and ODORDER.orderid = #{entity.orderid}
	        and ODORDER.istrans = #{entity.istrans}
	        and ODORDER.transcityname = #{entity.transcityname}
	        and ODORDER.memo = #{entity.memo}
	        and ODORDER.payid = #{entity.payid}
	        and ODORDER.pay = #{entity.pay}
	        and ODORDER.payed = #{entity.payed}
	        and ODORDER.isclose = #{entity.isclose}
	        and ODORDER.closedate = #{entity.closedate}
	        and ODORDER.closeuserno = #{entity.closeuserno}
	        and ODORDER.closereason = #{entity.closereason}


)

