select 

	from smuser 
	where smuser.ilineid in (

		select 
		smuser.ilineid
	    from smuser 

	        and smuser.ilineid = #{entity.ilineid}
	        and smuser.vcuserno = #{entity.vcuserno}
	        and smuser.vcusername = #{entity.vcusername}
	        and smuser.vctel = #{entity.vctel}
	        and smuser.vcemail = #{entity.vcemail}
	        and smuser.vcmobile = #{entity.vcmobile}
	        and smuser.vcpassword = #{entity.vcpassword}
	        and smuser.ideptid = #{entity.ideptid}
	        and smuser.badmin = #{entity.badmin}
	        and smuser.bactive = #{entity.bactive}
	        and smuser.dtlastupdatedate = #{entity.dtlastupdatedate}
	        and smuser.iroleid = #{entity.iroleid}
	        and smuser.iusertype = #{entity.iusertype}
	        and smuser.icustomerid = #{entity.icustomerid}
	        and smuser.vcuserno2 = #{entity.vcuserno2}
	        and smuser.dtdate = #{entity.dtdate}


)

